import React, { useState, useEffect, useContext, useRef } from 'react';
import { useParams } from 'react-router-dom';
import socket from '../../services/socket';
import api from '../../services/api';
import AuthContext from '../../context/AuthContext';
import './ChatRoom.css';

const ChatRoom = () => {
  const { userId } = useParams();
  const { user } = useContext(AuthContext);
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState('');
  const [room, setRoom] = useState('');
  const [otherUser, setOtherUser] = useState(null);
  const messagesEndRef = useRef(null);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  useEffect(() => {
    if (user && userId) {
      const roomId = [user.id, userId].sort().join('-');
      setRoom(roomId);
      socket.emit('joinRoom', { room: roomId });
      fetchMessages();
      fetchOtherUser();
    }

    socket.on('receiveMessage', (message) => {
      setMessages(prev => [...prev, message]);
    });

    return () => {
      socket.off('receiveMessage');
    };
  }, [user, userId]);

  const fetchMessages = async () => {
    try {
      const res = await api.get(`/chat/${userId}`);
      setMessages(res.data.data);
    } catch (error) {
      console.error('Error fetching messages:', error);
    }
  };

  const fetchOtherUser = async () => {
    try {
      const res = await api.get(`/users/${userId}`);
      setOtherUser(res.data.data);
    } catch (error) {
      console.error('Error fetching user:', error);
    }
  };

  const sendMessage = (e) => {
    if (e) e.preventDefault();
    if (newMessage.trim() && room) {
      socket.emit('sendMessage', {
        sender: user.id,
        receiver: userId,
        content: newMessage.trim(),
        room,
      });
      setNewMessage('');
    }
  };

  if (!user) return <div className="chat-room">Please log in to chat</div>;

  return (
    <div className="chat-room">
      <div className="chat-header">
        <h2>Chat with {otherUser ? otherUser.name : 'User'}</h2>
      </div>
      <div className="messages">
        {messages.length === 0 ? (
          <div className="no-messages">No messages yet. Start the conversation!</div>
        ) : (
          messages.map((msg, index) => {
            const isOwn = msg.sender._id === user.id || msg.sender === user.id;
            return (
              <div key={index} className={`message ${isOwn ? 'own' : 'other'}`}>
                <div className="message-bubble">
                  <p className="message-content">{msg.content}</p>
                  <small className="message-time">
                    {new Date(msg.timestamp).toLocaleTimeString([], { 
                      hour: '2-digit', 
                      minute: '2-digit' 
                    })}
                  </small>
                </div>
              </div>
            );
          })
        )}
        <div ref={messagesEndRef} />
      </div>
      <form className="message-input" onSubmit={sendMessage}>
        <input
          type="text"
          value={newMessage}
          onChange={(e) => setNewMessage(e.target.value)}
          placeholder="Type a message..."
          autoFocus
        />
        <button type="submit" disabled={!newMessage.trim()}>Send</button>
      </form>
    </div>
  );
};

export default ChatRoom;
